con:config.script:
//editing request, executing it and asserting response

import com.eviware.soapui.support.XmlHolder            //we always define it by default 
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext 

def name= testRunner.testCase.testSteps["PropertiesForAddEmployee"].getPropertyValue("Name")  
def Id= testRunner.testCase.testSteps["PropertiesForAddEmployee"].getPropertyValue("id")  
def age= testRunner.testCase.testSteps["PropertiesForAddEmployee"].getPropertyValue("age")  

def AddReq=testRunner.testCase.testSteps["addEmployee"].getPropertyValue("Request") 

def AddXml=new XmlHolder(AddReq)   // parsing the XML


// traversing through the XML and setting values
AddXml.setNodeValue("//typ:addEmployee/typ:name",name) 
AddXml.setNodeValue("//typ:addEmployee/typ:age",age) 
AddXml.setNodeValue("//typ:addEmployee/typ:id",Id) 


def NewAddXml=AddXml.getXml() 


// we pasted the newly editted XML to custom property
testRunner.testCase.testSteps["addEmployee"].setPropertyValue("Request",NewAddXml)


// -----------------execution code---------------
def variabeForRun1=testRunner.testCase.testSteps["addEmployee"]
def contextOfRequest1= new WsdlTestRunContext(variabeForRun1)

variabeForRun1.run(testRunner,contextOfRequest1)


// -------------------- assertion --------------------

def AddRes=testRunner.testCase.testSteps["addEmployee"].getPropertyValue("Response") 
def AddResXml=new XmlHolder(AddRes) 
def assertReturnvalue= AddResXml.getNodeValue("//ns:addEmployeeResponse/ns:return") 


// we are checking response's result with that of a custom property
assert assertReturnvalue==testRunner.testCase.getPropertyValue("AssertReturn")
